import React, { Component } from 'react';
import DataSource from 'devextreme/data/data_source';
import CustomStore from 'devextreme/data/custom_store';
import Chart, {
  ValueAxis,
  Label,
  Legend,
  Series,
  Size,
  Export,
  LoadingIndicator
} from 'devextreme-react/chart';
import SelectBox from 'devextreme-react/select-box';

type myProps = {
  temperature: any,
  palette: any,
  paletteIndex:any,
  monthWeather:any
}
class Axx extends React.Component<{}, myProps> {
  constructor(props: any) {
    super(props);
    this.state = {
      temperature:[6, 7, 8, 9, 10, 11, 12],
      palette :['#c3a2cc', '#b7b5e0', '#e48cba'],
      paletteIndex: 0,
      monthWeather: []
    }
  }

  componentDidMount(){
    var s = new DataSource({
      store: new CustomStore({
        load: () => {
          return fetch('./../data/js.json')
            .then(e => e.json())
            .catch(() => { throw 'Data Loading Error'; });
        },
        loadMode: 'raw'
      }),
      filter: ['t', '>', '6'],
      paginate: false
    })
    console.log(s)

    this.customizePoint();
  }

  customizePoint = () => {
    const color = this.state.palette[this.state.paletteIndex];
    // this.paletteIndex = this.paletteIndex === 2 ? 0 : this.paletteIndex + 1;
    console.log(color)

    // return { color };
  };

  render() {
    return (
      <>
      <p>The current time is {this.state.temperature}</p>
      <p>The current time is {this.state.palette}</p>
      <p>The current time is {this.state.paletteIndex}</p>
      <p>The current time is {this.state.monthWeather}</p>
      </>
    )
  }
}

export default Axx;
